import{_ as r,c as n,o as t,af as s}from"./chunks/framework.C1SSigD7.js";const c=JSON.parse('{"title":"Derbes Verstummen","description":"Eine Bühne im Staub, zwei Stimmen im Disput: über Kunst, die zu laut wurde, um noch gehört zu werden. Ein Dialog über das Verstummen der Derbheit – und das leise Erwachsen einer neuen Wahrhaftigkeit, jenseits von Zensur und Applaus.","frontmatter":{"title":"Derbes Verstummen","headline":"Das Echo der Überdröhnung","description":"Eine Bühne im Staub, zwei Stimmen im Disput: über Kunst, die zu laut wurde, um noch gehört zu werden. Ein Dialog über das Verstummen der Derbheit – und das leise Erwachsen einer neuen Wahrhaftigkeit, jenseits von Zensur und Applaus.","keywords":["Kunstfreiheit","Zensur","Authentizität","Gesellschaft","Wandel","Relevanz","Bühne","Somuncu","Kabarett","Reflexion"],"published_at":"2025-11-17T16:54Z"},"headers":[],"relativePath":"essay/derbes-verstummen.md","filePath":"essay/derbes-verstummen.md"}'),i={name:"essay/derbes-verstummen.md"};function a(b,e,u,m,d,l){return t(),n("div",null,e[0]||(e[0]=[s('<h1 id="derbes-verstummen" tabindex="-1">Derbes Verstummen <a class="header-anchor" href="#derbes-verstummen" aria-label="Permalink to &quot;Derbes Verstummen&quot;">​</a></h1><p><em>– Ein Dialog in einem fast leeren Theater</em></p><p>Leere Bühne.<br> Ein einzelner Stuhl,<br> ein Mikrofon,<br> das niemand mehr benutzt.<br> Die Luft ist still,<br> als würde sie auf eine Entschuldigung warten.</p><p>S. und Bertolt betreten nacheinander den Raum.<br> S. bleibt im Lichtkegel stehen,<br> Bertolt sitzt im Halbdunkel.</p><hr><p><em>S.:</em><br> Siehst du?<br> Genau so endet es –<br> mit Stille.<br> Nicht, weil niemand mehr etwas zu sagen hätte,<br> sondern weil keiner mehr etwas hören will,<br> das nicht in die Stimmung passt.<br> Die Kunst wird gesäubert,<br> geglättet,<br> moralisch überlegen.<br> Früher brauchte sie Mut.<br> Heute braucht sie Rücksicht.</p><p><em>Bertolt:</em><br> Vielleicht braucht sie heute eher Geduld.</p><p><em>S.:</em><br> Geduld?<br> Ich nenne das Angst.<br> Veranstalter, –<br> die absagen.<br> Publikum, –<br> das sich beschwert,<br> weil ein Satz stört.<br> Man darf nichts mehr wagen,<br> ohne zu verlieren.<br> Die Bühne hier –<br> auf ihr stand ich seit Jahren.<br> Heute –<br> Stuhlreihen voll Staub,<br> ungenutzte Vorhänge,<br> die sich nur bewegen,<br> wenn jemand die Tür schließt.</p><p><em>Bertolt:</em><br> Vielleicht ist das das wahre Dilemma:<br> Nicht,<br> dass man nichts mehr sagen darf,<br> sondern,<br> dass man immer noch glaubt,<br> gebucht werden zu müssen.</p><p><em>S.:</em><br> Ach bitte.<br> Ich war immer unbequem.<br> Das war meine Aufgabe.<br> Das war Kunst.</p><p><em>Bertolt:</em><br> Und wer hat dir die Aufgabe gegeben?</p><blockquote><p>Stille.<br> S. schaut ins Leere.</p></blockquote><p><em>S.:</em><br> Das Publikum.<br> Die Gesellschaft.<br> Ich war der,<br> der sagte,<br> was keiner sagen durfte.</p><p><em>Bertolt:</em><br> Und wenn jetzt jeder sagt,<br> was er denkt –<br> was bleibt dann vom Narren,<br> außer Nostalgie?</p><p><em>S.:</em><br> Du meinst,<br> das Volk hat mir den Platz genommen?</p><p><em>Bertolt:</em><br> Nicht genommen.<br> Nur geteilt.<br> Das Mikrofon wandert heute schneller.<br> Vielleicht war es nie deins,<br> sondern nur geliehen.</p><p><em>S.:</em><br> Aber wo bleibt dann das Risiko?<br> Die Reibung?<br> Das war doch das Herz der Kunst.</p><p><em>Bertolt:</em><br> Vielleicht ist das neue Risiko,<br> nicht mehr laut zu sein,<br> sondern authentisch.<br> Nicht zu schreien,<br> was man immer schrie,<br> sondern das eigene Schweigen zu verstehen.</p><p><em>S.:</em><br> Das klingt nach Kapitulation.</p><p><em>Bertolt:</em><br> Oder nach Wandel.<br> Du nennst es Zensur,<br> weil du glaubst,<br> jemand nähme dir etwas.<br> Aber niemand verbietet dir das Sagen –<br> sie kaufen nur kein Ticket mehr dafür.<br> Vielleicht gibt dir die Zeit nur zurück,<br> was du vergessen hast:<br> dass Kunst nicht Besitz ist,<br> sondern Bewegung.</p><blockquote><p>S. geht einige Schritte.<br> Die Bretter knarren.</p></blockquote><p><em>S.:</em><br> Und wenn sich alles bewegt –<br> wo ist dann mein Platz?</p><p><em>Bertolt:</em><br> Vielleicht genau hier.<br> Im Aushalten des Dazwischen.<br> Im Schweigen,<br> bis wieder etwas gesagt werden muss,<br> nicht nur gesagt werden will.</p><blockquote><p>Lange Pause.</p></blockquote><p><em>S.:</em><br> Und was,<br> wenn sie mich vergessen?</p><p><em>Bertolt:</em><br> Dann bleibt,<br> was wirklich war.<br> Nicht der Applaus,<br> sondern das Echo.<br> Es trifft nur später.<br> Und vielleicht anders,<br> als du dachtest.</p><blockquote><p>Das Licht flackert,<br> dann erlischt es.<br> Nur das Mikrofon blinkt kurz auf –<br> ein rotes Standby-Licht,<br> das atmet.</p></blockquote><hr><p><em>Ende.</em></p>',29)]))}const o=r(i,[["render",a]]);export{c as __pageData,o as default};
